'use strict';

const Assert = require( 'assert' );
const RandomWord = require( './RandomWord' );

if ( module === require.main ) {
 Assert( RandomWord );

 var word0 = RandomWord( 8 );
 Assert( typeof ( word0 ) === 'string' );
 Assert( word0.length === 8 );
 console.log( word0 );

 var word1 = RandomWord( 8 );
 Assert( typeof ( word1 ) === 'string' );
 Assert( word1.length === 8 );
 console.log( word1 );

 Assert( word0 !== word1 );

 var word2 = RandomWord( 9 );
 Assert( typeof ( word2 ) === 'string' );
 Assert( word2.length === 9 );
 console.log( word2 );

 var word3 = RandomWord( 42 );
 Assert( typeof ( word3 ) === 'string' );
 Assert( word3.length === 42 );
 console.log( word3 );
}